function LangBox({children}: {children: React.ReactNode}){
    return <div className="flex items-center justify-center">
        {children}
    </div>
}
function SectionTitle({children}: {children: React.ReactNode}) {
    return <h1 className="text-slate-400 font-[ClashGrotesk] tracking-wider font-black text-3xl md:text-5xl mb-11">
        {children}
    </h1>
}
function LanguageSection({children, steps, step, l}: {children: React.ReactNode, steps: number, step: number, l?: boolean}){
    return <div className={`absolute py-[15%] w-full opacity-0 absolute translate-y-40 invisible transition-all ${(l && steps > step) || steps == step ? "opacity-100 visible !translate-y-0":""}`}>
        {children}
    </div>
}